//Back-end complete function Template for C++

// Function to find the sum of elements in the array.
class Solution {
  public:

    void sumArray(vector<int> &arr) {
        int n = arr.size();
        int sum = 0;
        for (int i = 0; i < n; i++)
            sum += arr[i];

        // Calculating the difference between the sum of elements and each element
        // and updating the array with the differences.
        for (int i = 0; i < n; i++)
            arr[i] = sum - arr[i];
    }
};

class Solution {
    public void sumArray(List<Integer> arr) {
        int sum = arr.stream().mapToInt(Integer::intValue).sum();
        for (int i = 0; i < arr.size(); i++) {
            arr.set(i, sum - arr.get(i));
        }
    }
}

class Solution:

    def sumArray(self, arr):
        total_sum = sum(arr)
        arr[:] = [total_sum - x for x in arr]

class Solution {
    sumArray(arr) {
        let sum = arr.reduce((acc, val) => acc + val, 0);
        for (let i = 0; i < arr.length; i++) {
            arr[i] = sum - arr[i];
        }
    }
}
