// Back-end complete function Template for C++

class Solution{
public:
    // Function to count pairs
    int pairCount(int N, int A[], int B[], int C[])
    {
        int count = 0;
        // Initializing hash table for positive and negative numbers
        int hash[101][2] = {{0}};

        // Loop to fill the hash table
        for(int i = 0; i < N;i++){
            // If current number is negative
            if(C[i] < 0){
                int temp = -C[i];
                // Marking the negative number in the hash table
                hash[temp][1] = 1;
            }
            // If current number is positive
            else{
                // Marking the positive number in the hash table
                hash[C[i]][0] = 1;
            }
        }

        // Loop to count the pairs
        for(int i = 0; i < N;i++){
            // If A[i] < B[i]
            if(A[i] < B[i]){
                // Checking if the difference between B[i] and A[i] exists in the hash table for negative numbers
                if(hash[B[i]-A[i]][1])
                    count++;
            }
            // If A[i] >= B[i]
            else{
                // Checking if the difference between A[i] and B[i] exists in the hash table for positive numbers
                if(hash[A[i]-B[i]][0])
                    count++;
            }
        }

        // Returning the count of pairs
        return count;
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to count pairs from the two arrays.
    public int pairCount(int N, int[] A, int[] B, int[] C)
    {
        int count = 0;
        // Initializing hash table for positive and negative numbers
        int[,] hash = new int[101,2];

        // Loop to fill the hash table
        for(int i = 0; i < N;i++){
            // If current number is negative
            if(C[i] < 0){
                int temp = -C[i];
                // Marking the negative number in the hash table
                hash[temp,1] = 1;
            }
            // If current number is positive
            else{
                // Marking the positive number in the hash table
                hash[C[i],0] = 1;
            }
        }

        // Loop to count the pairs
        for(int i = 0; i < N;i++){
            // If A[i] < B[i]
            if(A[i] < B[i]){
                // Checking if the difference between B[i] and A[i] exists in the hash table for negative numbers
                if(hash[B[i]-A[i],1] != 0)
                    count++;
            }
            // If A[i] >= B[i]
            else{
                // Checking if the difference between A[i] and B[i] exists in the hash table for positive numbers
                if(hash[A[i]-B[i],0] != 0)
                    count++;
            }
        }

        // Returning the count of pairs
        return count;
    }

}
