//Back-end complete function template in C++

class Solution
{
	public:
		//Function to calculate the sum of an arithmetic progression.
		int sum_of_ap(int n,int a, int d)
		{
			//calculating the sum using the formula: n/2 * [2a + (n-1)d]
			int sum = (n * (2 * a + (n - 1) * d))/2;
			//returning the sum of the arithmetic progression.
			return sum;
		}
};

