// back-end complete function template in C++

class Solution
{
	public:
		// Function to calculate the sum of an arithmetic progression
		long sum_of_ap(long n, long a, long d)
		{
			// Calculate the sum of the arithmetic progression using the formula
			long sum = (n * (2 * a + (n - 1) * d)) / 2;

			// Return the sum
			return sum;
		}
};
