/**
 * @param {number[]} arr - The array of numbers to sum
 * @return {number} - The sum of the array elements
 */
class Solution {
    arraySum(arr) {
        // Return sum using the reduce function
        return arr.reduce((acc, curr) => acc + curr, 0);
    }
}
