//Back-end complete function template for C++

class Solution{
public:
	long long sumBitDifferences(int arr[], int n) {
	    long long ans = 0; // Initialize result

	    // traverse over all bits
	    for (int i = 0; i < 32; i++) {
	        // count number of elements with i'th bit set
	        int count = 0;
	        for (int j = 0; j < n; j++)
	            if ((arr[j] & (1 << i)))
	                count++;

	        // Add "count * (n - count) * 2" to the answer
	        ans += ((long long) count * (long long) (n - count) * 2LL);
	    }

	    return ans;
	}
};

class Solution {
    long sumBitDifferences(int[] arr, int n) {
        long ans = 0; // Initialize result

        // traverse over all bits
        for (int i = 0; i < 32; i++) {
            // count number of elements with i'th bit set
            int count = 0;
            for (int j = 0; j < n; j++)
                if ((arr[j] & (1 << i)) != 0)
                    count++;

            // Add "count * (n - count) * 2" to the answer
            ans += ((long) count * (long) (n - count) * 2);
        }

        return ans;
    }
}

class Solution:
    def sumBitDifferences(self, arr, n):
        ans = 0  # Initialize result

        # Traverse over all bits
        for i in range(32):
            # Count number of elements with i'th bit set
            count = 0
            for j in range(n):
                if arr[j] & (1 << i):
                    count += 1

            # Add "count * (n - count) * 2" to the answer
            ans += count * (n - count) * 2

        return ans

class Solution
{
    public long sumBitDifferences(int[] arr, int n)
    {
        long ans = 0;

        for (int i = 0; i < 32; i++)
        {
            int count = 0;
            for (int j = 0; j < n; j++)
            {
                if ((arr[j] & (1 << i)) != 0)
                {
                    count++;
                }
            }
            ans += ((long)count * (long)(n - count) * 2);
        }

