//Back-end complete function Template for C++
class Solution {
  public:
    long long int canBeSumofConsec(long long int n) { 
    // We basically return false if n is a 
    // power of two 
    return ((n&(n-1)) && n); 
    }
 
};

//User function Template for C#

class Solution
{
    //Complete this function
    public long canBeSumofConsec(long n)
    {
        // We basically return false if n is a 
        // power of two 
        return ((n & (n - 1)) != 0 && n != 0) ? 1 : 0;
    }
}
