class Solution {
  public:
    int sumOfDigits(int N) {
        int count = 0;
        while (N > 0) {
            // extract the last digit
            int b = N % 10;
            // add it to count
            count += b;
            // remove the last digit
            N = N / 10;
        }
        return count;
    }
};

