class Solution {

    sumOfDigits(N) {
        let sum = 0;       // Initialize a variable to store the sum of the digits.
        while (N > 0) {    // Continue the loop until N becomes 0.
            sum += N % 10; // Add the last digit of N to the sum.
            N = Math.floor(
                N /
                10); // Remove the last digit of N by dividing it by 10 and update N.
        }
        return sum; // Return the sum of the digits.
    }
}
