//Back-end complete function template in C++
class Solution {
  public:
    // Function to calculate the sum of distinct elements in the array.
    int sumOfDistinct(vector<int>& arr) {
        unordered_set<int> seen;
        int sum = 0;
        for (int num : arr) {
            if (seen.find(num) == seen.end()) {
                sum += num;
                seen.insert(num);
            }
        }
        return sum;
    }
};

class Solution {
    // Function to calculate the sum of distinct elements in the array.
    public int sumOfDistinct(int[] arr) {
        // HashSet to store distinct elements.
        HashSet<Integer> set = new HashSet<>();
        // Variable to store the sum of distinct elements.
        int sum = 0;
        // Iterating over the array.
        for (int num : arr) {
            // Adding element to sum if it's not already in the set.
            if (!set.contains(num)) {
                sum += num;
                set.add(num);
            }
        }
        // Returning the sum of distinct elements.
        return sum;
    }
}

class Solution:
    # Function to calculate the sum of distinct elements in the array.
    def sumOfDistinct(self, arr):
        return sum(set(arr))  # Use set to get distinct elements, then sum them

class Solution {
    // Function to calculate the sum of distinct elements in the array.
    sumOfDistinct(arr) {
        return arr.filter((value, index) => arr.indexOf(value) === index)
            .reduce((acc, curr) => acc + curr, 0);
    }
}
