// User function template for C++
class Solution {
  public:
    int findSum(vector<int>& arr) {
        int n = arr.size();
        int sum = 0;

        // Hash to store all element of array
        unordered_set<int> s;
        for (int i = 0; i < n; i++) {
            if (s.find(arr[i]) == s.end()) {
                sum += arr[i];
                s.insert(arr[i]);
            }
        }

        return sum;
    }
};

class Solution {
    // Find the sum of all non-repeated elements in an array
    public int findSum(int[] arr) {
        int n = arr.length;
        int sum = 0;

        // HashSet to store all elements of the array
        HashSet<Integer> set = new HashSet<>();
        for (int i = 0; i < n; i++) {
            // If the element is not already in the set, add it to the sum
            // and insert it into the set
            if (!set.contains(arr[i])) {
                sum += arr[i];
                set.add(arr[i]);
            }
        }

        return sum;
    }
}

