class Solution {
  public:
    long long sumOfFifthPowers(long long N) {
        long long ans = 0;
        // using formula to calculate sum of
        // fifth powers of first N natural numbers.
        ans = ((N * N) * (N + 1) * (N + 1) * (2 * N * N + 2 * N - 1)) / 12;
        return ans;
    }
};

