class Solution
{
	public:
		// Function to calculate the sum of a geometric progression
		long long int sum_of_gp(long n,long a, long r)
		{
		    // If common ratio is 1 then simply do first_term * number_terms
			if(r == 1)
				return (long)(a * n);
			// Using GP series sum formula.
			long long int sum = 1;
			for(int i = 1; i<= n; i++)
			{
				sum *= r;
			}
			sum -= 1;
			sum *= a;
			sum /= (r-1);
			return sum;
		}
};

