class Solution
{
    //Complete this function
    //Function to find the sum of the geometric progression.
    public long sum_of_gp(long n, long a, long r)
    {
        // If common ratio is 1 then simply do first_term * number_terms
        if (r == 1)
            return (a * n);
        // Using GP series sum formula.
        long sum = 1;
        for (long i = 1; i <= n; i++)
        {
            sum *= r;
        }
        sum -= 1;
        sum *= a;
        sum /= (r - 1);
        return sum;
    }
}
