class Solution {
  public:
    vector<int> findSum(int n) {
        // find even_count
        int even_cnt = n / 2;
        // find odd_count
        int odd_cnt = (n + 1) / 2;
        // find sum using AP series.
        int even_sum = (even_cnt * (4 + (even_cnt - 1) * 2)) / 2;
        int odd_sum = (odd_cnt * (2 + (odd_cnt - 1) * 2)) / 2;
        vector<int> res(2);
        res[1] = even_sum;
        res[0] = odd_sum;
        return res;
    }
};

class Solution {
    public List<int> findSum(int n) {
