//Back-end complete function Template for C++

class Solution {
  public:
    // Function to check if a number is prime or not.
    int isPrime(int x) {
        if (x == 2 || x == 3 || x == 5 || x == 7)
            return x;
        return 0;
    }

    // Function to calculate the sum of prime digits in a number.
    int primeSum(int N) {
        int ans = 0;
        while (N > 0) {
            int x = (N % 10);
            ans += isPrime(x);
            N /= 10;
        }
        return ans;
    }
};

