// Back-end complete function Template for C++

class Solution {
  public:
    // Function to calculate the product of pairs.
    int productOfPairs(int N, vector<int> A) {
        long long ans = 0, x = 0;
        int Mod = (int)(1e9 + 7);

        // Iterating over all the elements in the vector.
        for (int i = 0; i < N; i++) {
            // Updating the answer by multiplying x and the current element and taking modulo.
            ans += (x * A[i]) % Mod;
            ans %= Mod;

            // Updating x by adding the current element and taking modulo.
            x += A[i];
            x %= Mod;
        }
        // Returning the final answer.
        return ans;
    }
};

// Back-end complete function Template for Java

class Solution {
    // function to calculate the product of pairs
    int productOfPairs(int N, int A[]) {
        long ans = 0, x = 0;
        int Mod = (int)(1e9 + 7);
        // iterating over the array
        for (int i = 0; i < N; i++) {
            // adding the product of x and A[i] to the answer and taking modulo
            ans += (x * A[i]) % Mod;
            ans %= Mod;
            // updating x by adding A[i] and taking modulo
            x += A[i];
            x %= Mod;
        }
        // returning the answer as an integer
        return (int)ans;
    }
};

