/**
 * @param {number} N
 * @param {number[]} A
 * @returns {number}
*/

class Solution {
    //Function to find product of all pairs of array elements and
    //store it in a new array.
    productOfPairs(N, A) {
        let ans=0;
        let x=0;
        let Mod=(Math.pow(10, 9)+7);

        //iterating over each element in the given list.
        for (let i of A){
            //calculating the product of current element and x,
            //and adding it to the answer.
            ans+=(x*i)%Mod;
            ans%=Mod;

            //updating x as the sum of current element and previous x.
            x+=i;
            x%=Mod;
        }

        //returning the final answer.
        return ans;
    }
}

