class Solution{
	public:
	long long int sumofproduct(int n)
	{
	    long long int ans = 0;
	 
	    // Iterating x from 1 to n
	    for (long long int x = 1; x <= n; x++)
	    {
	        // Finding y = n/x.
	        long long int y = n/x;
	 
	        // Adding product of x and y to answer.
	        ans += (y * x);
	    }
	 
	    return ans;
	}  
};

