class Solution
{
    //Complete this function
    //Function to calculate the sum of products of all possible pairs of array elements.
    public long sumofproduct(int n)
    {
        long ans = 0;

        // Iterating x from 1 to n
        for (long x = 1; x <= n; x++)
        {
            // Finding y = n/x.
            long y = n/x;

            // Adding product of x and y to answer.
            ans += (y * x);
        }

        return ans;
    }
}
