//Back-end complete function Template for C++

class Solution{
	public:
	//Helper function to calculate the sum of the elements up to x using the formula for the sum of an arithmetic sequence.
	long long int helper(long long int x){
		long long int sum = (x * (x + 1))/2;
		return sum;
	}

   	//Function to calculate the sum of the nth row in a given pattern.
   	int Nth_rowSum(int n){
   		long long int k = n;

   		//Calculating the values of x and y based on the given pattern.
   		long long int x = (k * (k - 1));
   		long long int y = x + 2 * k;

   		//Using the helper function to calculate the sum of the elements from x to y.
   		long long int ans = helper(y) - helper(x);

   		//Taking the modulo 1000000007 of the calculated sum.
   		ans %= 1000000007;

   		//Returning the sum as an integer.
   		return int(ans);
   	}    
};

class Solution
{
    //Helper function to calculate the sum of the elements up to x using the formula for the sum of an arithmetic sequence.
    public long helper(long x)
    {
        long sum = (x * (x + 1))/2;
        return sum;
    }

    //Function to calculate the sum of the nth row in a given pattern.
    public int Nth_rowSum(int n)
    {
        long k = n;

        //Calculating the values of x and y based on the given pattern.
        long x = (k * (k - 1));
        long y = x + 2 * k;

        //Using the helper function to calculate the sum of the elements from x to y.
        long ans = helper(y) - helper(x);

        //Taking the modulo 1000000007 of the calculated sum.
        ans %= 1000000007;

        //Returning the sum as an integer.
        return (int)ans;
    }    
}
