//Back-end complete function Template for C++

class Solution {
  public:
    int unique_elements(vector<int> nums) {
        // Your code here
        int ans = 0;
        map<int, int> mp;
        for (auto i : nums) {
            mp[i]++;
        }
        for (auto i : nums)
            if (mp[i] == 1)
                ans += i;
        return ans;
    }
};
