// Back-end complete function Template for C++

class Solution {
  public:
    // Function to calculate modified sum
    long long modifiedSum(int N, int M, int K) {
        long long ans = 0;
        int mod = (int)(1e9 + 7);

        // iterating M times
        for (int i = 0; i < M; i++) {
            long long temp = N + K;

            // calculating ans using temp
            ans = temp * (temp + 1);
            ans %= mod;

            // updating N with ans for next iteration
            N = ans;
        }
        return ans;
    }
};

// Back-end complete function Template for Java

class Solution {
    long modifiedSum(int N, int M, int K) {
        long ans = 0; // initialize a variable to store the answer
        int mod = (int)(1e9 + 7); // define a variable for the modulo value

        for (int i = 0; i < M; i++) { // loop M times
            long temp = N + K; // calculate the sum of N and K and store it in a temporary variable
            ans = temp * (temp + 1); // calculate the product of temp and its next value
            ans %= mod; // apply the modulo operation to the result and store it in ans
            N = (int)ans; // update the value of N with ans
        }

        return ans; // return the final answer
    }
}

