class Solution {
  public:
    vector<int> find(int l, int b, int h) {
        int area = 2 * (l * h + b * h + l * b);
        int volume = (l * b * h);
        vector<int> res;
        res.push_back(area);
        res.push_back(volume);
        return res;
    }
};

class Solution {
    public int[] find(int l, int b, int h) {
        int area = 2 * (l * h + b * h + l * b);
        int volume = (l * b * h);
        return new int[] {area, volume};
    }
}

class Solution {
    public List<int> find(int l, int b, int h) {
        int L = l, B = b, H = h;
        int area = 2 * (L * H + B * H + L * B);
        int volume = L * B * H;
        List<int> res = new List<int>();
        res.Add(area);
        res.Add(volume);
        return res;
    }
}
