class Solution{
public:    
    int swapBits(int x, int p1, int p2, int n)
    {
        unsigned int set1 =  (x >> p1) & ((1U << n) - 1);
     
        unsigned int set2 =  (x >> p2) & ((1U << n) - 1);
     
        unsigned int xor1 = (set1 ^ set2);
     
        xor1 = (xor1 << p1) | (xor1 << p2);
     
        unsigned int result = x ^ xor1;
     
        return result;
    }
};

