//User function Template for javascript
/**
 * @param {number} X
 * @param {number} P1
 * @param {number} P2
 * @param {number} N
 * @returns {number}
*/

class Solution {
   //Function to swap bits in a given number.
   swapBits(X, P1, P2, N) {
       //Extracting the bits from P1
       let n_bits_from_p1 = (X >> P1) & ((1 << N) - 1);
       
       //Extracting the bits from P2
       let n_bits_from_p2 = (X >> P2) & ((1 << N) - 1);
       
       //Computing the mask by XORing the bits extracted from P1 and P2
       let mask = n_bits_from_p1 ^ n_bits_from_p2;
       
       //Creating a swapper mask by shifting the mask to the correct positions
       let swapper_mask = (mask << P1) | (mask << P2);
       
       //Performing the bit swapping operation by XORing the swapper mask with the original number
       return swapper_mask ^ X;
   }
}
