class Solution
{
    //Function to swap the digits of two given strings.
    public void swapDigits(ref string N1, ref string N2)
    {
        int n1 = N1.Length;
        int n2 = N2.Length;

        char firstN1 = N1[0];
        char lastN1 = N1[n1-1];
        char firstN2 = N2[0];
        char lastN2 = N2[n2-1];

        string newN1 = lastN2 + N1.Substring(1, n1-2) + firstN2;
        string newN2 = lastN1 + N2.Substring(1, n2-2) + firstN1;

        N1 = newN1;
        N2 = newN2;
    }
}
