//swapper function to swap two Person objects
void swapper(Person **obj1, Person **obj2)
{
    //creating a temporary Person object
    Person *temp = new Person();

    //assigning the value of obj1 to temp
    temp = *obj1;

    //assigning the value of obj2 to obj1 and temp to obj2
    *obj1 = *obj2;
    *obj2 = temp;
}
