class Solution{
public:
    int andGate(vector<int> arr,int N){
        for(int i = 0 ;i<N ;i++){
            // if we find a bit as 0 the resulting 
            // answer will always be 0
            if(arr[i] == 0)
                return 0;
        }
        // if no 0 is present then answer is 1
        return 1;
    }
};

