//Back-end complete function template for C++

class Solution{   
public:
    //Function to swap the bits and determine the game winner.
    int swapBitGame(long long N){
        
        int ans = 0;
        //Iterating from 40 to 0 to check each bit.
        for(long long int i = 40 ; i >=0 ; i--)
        {
            //If the bit is set in the number N, increment answer.
            if((1LL<<i) & N)
            {
                ans++;
            }
        }
        //If the number of set bits is odd, return 1 else return 2.
        if(ans%2)
        {
            return 1;
        }
        else return 2;
    }
};

class Solution
{
    public int swapBitGame(long N)
    {
        int ans = 0;
        for (int i = 40; i >= 0; i--)
        {
            if (((1L << i) & N) != 0)
            {
                ans++;
            }
        }
        if (ans % 2 != 0)
        {
            return 1;
        }
        else return 2;
    }
}
