#include <iostream>
using namespace std;

int main() {
    int number;
    cin >> number;
    if (number > 100)
        cout << "Big" << endl;
    else if (number < 10)
        cout << "Small" << endl;
    else
        cout << "Number" << endl;
    return 0;
}

