// Back-end complete function Template for C++

class Solution{
public:
    // Function to find the fastest car based on their velocities.
    int fastest(int N, int V1, int V2, int V3){
        // Initialize the maximum velocity variable.
        double maxi = 0.0;

        // Calculate the velocities in different directions.
        double x = (double)V2/sqrt(2), y = (double)V3/sqrt(2);
        double z = (double)V1*1.0;

        // Find the maximum velocity.
        maxi = max(maxi, max(z, max(x, y)));

        // Check which car has the maximum velocity and return its index.
        if(z == maxi)
            return 1;
        if(x == maxi)
            return 2;
        return 3;
    }
};
