class Solution{
public:
    // Function to find the minimum number that is divisible by X, Y, and Z and has N digits
    long long int minNumber(int X, int Y, int Z, int N)
    {
        long long int x = X, y = Y, z = Z, n = N;

        // Calculating the least common multiple of X and Y
        long long int lcm_b = ((x*y)/__gcd(x,y));

        // Calculating the least common multiple of X, Y, and Z
        long long int lcm = ((lcm_b*z)/__gcd(lcm_b,z));

        // Calculating the minimum value that has N digits
        long long int val = (long long int)pow(10,n-1);
        long long int val1 = (long long int)pow(10,n);

        // Calculating the number of times lcm has to be multiplied to get a value greater than or equal to val
        long long int m = val/lcm;
        // Incrementing m if m*lcm is not equal to val
        if(m*lcm != val)
            m++;

        // Checking if the minimum value with N digits is greater than or equal to val1
        if(m*lcm >= val1)
            return -1LL;
        else
            return (m*lcm);
    }
};

class Solution
{
    // Function to calculate the greatest common divisor of two numbers
    private long gcd(long a, long b)
    {
        if(b == 0)
            return a;
        else
            return gcd(b, a % b);
    }

    // Function to find the minimum number that is divisible by X, Y, and Z and has N digits
    public long minNUmber(int X, int Y, int Z, int N)
    {
        long x = X, y = Y, z = Z, n = N;

        // Calculating the least common multiple of X and Y
        long lcm_b = ((x * y) / gcd(x, y));

        // Calculating the least common multiple of X, Y, and Z
        long lcm = ((lcm_b * z) / gcd(lcm_b, z));

        // Calculating the minimum value that has N digits
        long val = (long)Math.Pow(10, n - 1);
        long val1 = (long)Math.Pow(10, n);

        // Calculating the number of times lcm has to be multiplied to get a value greater than or equal to val
        long m = val / lcm;
        // Incrementing m if m*lcm is not equal to val
        if (m * lcm != val)
            m++;

        // Checking if the minimum value with N digits is greater than or equal to val1
        if (m * lcm >= val1)
            return -1L;
        else
            return (m * lcm);
    }
}
