class Solution
{
    public:
    //Function to find minimum number of characters which Ishaan must insert  
    //such that string doesn't have three consecutive same characters.
    int modified(string a)
    {
        int ans, same;
        ans=0;
        same=1;

        //checking for any three consecutive same characters and if they are
        //found then we increment the count of characters to be added.
        for(int i=1;a[i];i++)
        {
            if(a[i]==a[i-1]){
                same++;
            }
            else{
                ans+=(same-1)/2;
                same=1;
            }
        }
        ans += (same-1)/2;
        //returning the answer.
        return ans;
    }

};

class Solution
{
    //Function to find minimum number of characters which Ishaan must insert  
    //such that string doesn't have three consecutive same characters.
    public static long modified(String a)
    {
        long ans = 0, same = 1;

        //checking for any three consecutive same characters and if they are
        //found then we increment the count of characters to be added.
        for(long i = 1; i < a.length(); i++)
        {
            if(a.charAt((int)i) == a.charAt((int)i-1))
                same++;
            else
            {
                ans += (same-1)/2;
                same = 1;
            }
        }
        ans += (same-1)/2;
        //returning the answer.
        return ans;
    }
}

