class Solution {
  public:
    //Function to calculate the result of a certain task.
    long long modTask(long long N) {
        
        //Calculating the answer by dividing N by 2 and adding 1.
        long long ans = N/2 + 1 ;
        
        //Returning the answer.
        return ans;
    }
};

