//Back-end complete function template in C++

class Solution {
  public:
    //Function to change the base of a number.
    long long changeBase(int N , int K) {

        vector<int>a;

        //Converting the number N to base K.
        while(N)
        {
            a.push_back(N%K);
            N/=K;
        }

        long long ans = 0;

        //Calculating the decimal equivalent of the base K number.
        for(int i = a.size()-1 ; i>=0 ; i--)
        {
            ans+=a[i];
            ans*=10;
        }

        ans /= 10;

        //Returning the final decimal equivalent.
        return ans;
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    public long changeBase(int N , int K)
    {
        List<int> a = new List<int>();

        //Converting the number N to base K.
        while(N > 0)
        {
            a.Add(N%K);
            N /= K;
        }

        long ans = 0;

        //Calculating the decimal equivalent of the base K number.
        for(int i = a.Count-1; i >= 0; i--)
        {
            ans += a[i];
            ans *= 10;
        }

        ans /= 10;

        //Returning the final decimal equivalent.
        return ans;
    }
}
