class Solution {
  public:
    // Function to calculate the remaining circumference of a circle
    // after a certain number of degrees are removed.
    double remainingCircle(double R, int N, int M) {
        // Conversion factor to convert degrees to radians
        double f = 3.14 / 180.0;
        // Converting integer values to double for calculations
        double n = N, m = M;
        // Calculating the degrees to be removed from the initial circle
        double x = (n * 360) / m;
        // Calculating the remaining degrees in the circle
        double y = 360.0 - x;
        // Calculating the remaining circumference of the circle
        return R * y * f;
    }
};

