class Solution {
    // Function to calculate the remaining circle after N and M divided the circle
    double remainingCircle(double R, int N, int M) {
        // Calculating the value of pi in radians
        double f = 3.14 / 180.0;
        // Converting N and M to double
        double n = N, m = M;
        // Calculating the angle covered by N in the circle
        double x = (n * 360) / m;
        // Calculating the remaining angle in the circle
        double y = 360.0 - x;
        // Calculating the remaining area of the circle using the radius, remaining angle, and pi
        return R * y * f;
    }
};
