#include <stdio.h>

int main() {
    int a;
    scanf("%d", &a);
    float b;
    scanf("%f", &b);
    double c;
    scanf("%lf", &c);
    long d;
    scanf("%ld", &d);

    // print the size of a, b, c, d using sizeof() operator
    printf("%zu %zu %zu %zu", sizeof(a), sizeof(b), sizeof(c), sizeof(d));

    return 0;
}

#include <iostream>
using namespace std;

int main() {
    int a;
    float b;
    double c;
    long long d;

    cin >> a >> b >> c >> d;

    // print the size of a, b, c, d using sizeof() operator
    cout << sizeof(a) << " " << sizeof(b) << " " << sizeof(c) << " " << sizeof(d);

    return 0;
}
