//Back-end complete function Template for C++

void dataTypes(int a, float b, double c, long long l, string d){

    //calculating the division of float and double
    float p = b/c;

    //calculating the division of float and int
    double q = b/a;

    //calculating the division of double and int
    int r = c/a;

    //calculating the sum of int and long long
    long long m = r + l;

    //printing the size of float, double, int, and long long
    cout << sizeof(p) << " " << sizeof(q) << " " << sizeof(r) << " " << sizeof(m) << endl;

    //printing the size of string and the size of one character in the string
    cout << sizeof(d) << " " << sizeof(d[3]) << endl;

}
