class Solution{
public:
    //Function to determine the output of XOR gate.
    int xorGate(vector<int> arr,int N){
        int count = 0;
        //Iterating over the elements in 'arr'.
        for(int i = 0 ;i<N ;i++){
            //Checking if the element is 1.
            //If yes, increment the count.
            if(arr[i] == 1)
                count++;
        }
        //If count is even, the output is 0.
        //If count is odd, the output is 1.
        return count%2;
    }
};

