//Back-end complete function template for C++

class Solution{
public:
    //Function to remove 3 consecutive duplicates from the given string.
    string remove3ConsecutiveDuplicates(string S)
    {
        string ans = "";
        for (int i = 0; i < S.size(); ++i) 
        {
            ans += S[i];

            //Checking if the last three characters are duplicates.
            if (ans.size() > 2) 
            {
                int sz = ans.size();
                if (ans[sz - 1] == ans[sz - 2] && 
                    ans[sz - 2] == ans[sz - 3]) {
                    ans.pop_back();
                    ans.pop_back();
                    ans.pop_back();
                }
            }
        }

        //If no characters are left in the string, return -1.
        if(ans.size() == 0)
            return "-1";

        //Return the modified string.
        return ans;

    }
};

