class Solution
{
    //Complete this function
    //Function to remove all three consecutive duplicates from the string.
    public string remove3ConsecutiveDuplicates(string s)
    {
        string ans = "";
        for (int i = 0; i < s.Length; ++i)
        {
            ans += s[i];

            //Checking if the last three characters are duplicates.
            if (ans.Length > 2)
            {
                int sz = ans.Length;
                if (ans[sz - 1] == ans[sz - 2] &&
                    ans[sz - 2] == ans[sz - 3])
                {
                    ans = ans.Remove(sz - 3);
                }
            }
        }

        //If no characters are left in the string, return -1.
        if (ans.Length == 0)
            return "-1";

        //Return the modified string.
        return ans;
    }
}
