//Back-end complete function template in C++

class Solution{
	public:
	//Function to count the number of ways.
	int countWays(int N)
	{
		//variable to store the modulo value.
		int MOD = 1000000007;

		//variables for counting odd and even numbers.
		long long int count, odd = N / 2, even; 

		//if N is odd, increment the count of odd numbers.
	    if (N & 1) 
	        odd = N / 2 + 1; 

	    //calculate the count of even numbers.
	    even = N / 2; 

	    // Case 1: 2 odds and 1 even 
	    //calculate the count of ways for this case.
	    count = (((odd * (odd - 1)) / 2) * even) % MOD; 

	    // Case 2: 3 evens 
	    //calculate the count of ways for this case and add it to the count.
	    count = (count + ((even * (even - 1) * (even - 2)) / 6)) % MOD;

	    //returning the final count.
	    return count; 
	}
};

//User function Template for C#

class Solution
{
    //Complete this function
    public int countWays(int N)
    {
        //variable to store the modulo value.
        int MOD = 1000000007;

        //variables for counting odd and even numbers.
        long count, odd = N / 2, even;

        //if N is odd, increment the count of odd numbers.
        if (N % 2 == 1) 
            odd = N / 2 + 1; 

        //calculate the count of even numbers.
        even = N / 2; 

        // Case 1: 2 odds and 1 even 
        //calculate the count of ways for this case.
        count = (((odd * (odd - 1)) / 2) * even) % MOD; 

        // Case 2: 3 evens 
        //calculate the count of ways for this case and add it to the count.
        count = (count + ((even * (even - 1) * (even - 2)) / 6)) % MOD;

        //returning the final count.
        return (int)count; 
    }
}
