//Back-end complete function Template for C++

class Solution {
  public:
    string TieBreak(vector<string>& arr) {
        int n = arr.size();
        string winner = "";
        unordered_map<string, int> ump;
        for (int i = 0; i < n; i++)
            ump[arr[i]]++;

        int maxVotes = 0;
        for (auto itr = ump.begin(); itr != ump.end(); itr++) {
            if ((itr->second > maxVotes) ||
                ((itr->second == maxVotes) && (itr->first < winner))) {
                maxVotes = itr->second;
                winner = itr->first;
            }
        }
        return winner;
    }
};

java.full_func:
//Back-end complete function Template for Java

class Solution {
    public static String TieBreak(String arr[]) {
        // Initializing winner to an empty String
        String winner = "";
        int n = arr.length;
        // Declaring and Initializing a HashMap
        HashMap<String, Integer> mp = new HashMap<>();

        for (int i = 0; i < n; i++) {
            // incrementing count if HashMap
            // already contains the name of the
            // candidate
            if (mp.containsKey(arr[i])) {
                mp.put(arr[i], mp.get(arr[i]) + 1);
            } else // initializing the count of the candidate votes with 1
            {
                mp.put(arr[i], 1);
            }
        }

        int maxVotes = 0;

        // running a loop to find the person's
        // name who has the maxVotes
        // in case of tie, we choose the
        // person who has lower lexogrophical name
        for (Map.Entry mapElement : mp.entrySet()) {
            String key = (String)mapElement.getKey();
            int value = ((int)mapElement.getValue());

            if (maxVotes < value ||
                ((value == maxVotes) && (key.compareTo(winner) < 0))) {
                maxVotes = value;
                winner = key;
            }
        }

        // returing the name of the winner
        return winner;
    }
}
