// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the time gap between two given time strings
    string timeGap(string st, string et){
        string ans = "";

        // Calculating the time gap in seconds
        int tg = ((et[0]-st[0])*10+(et[1]-st[1]))*3600+
                 ((et[3]-st[3])*10+et[4]-st[4])*60+((et[6]-st[6])*10+et[7]-st[7]);

        // Calculating the hours component of the time gap
        int x = tg/3600;
        if(x > 9)
            ans += (to_string(x)+":");
        else
            ans += ("0"+to_string(x)+":");

        // Calculating the minutes component of the time gap
        tg = tg%3600;
        x = tg/60;
        if(x > 9)
            ans += (to_string(x)+":");
        else
            ans += ("0"+to_string(x)+":");

        // Calculating the seconds component of the time gap
        tg = tg%60;
        if(tg > 9)
            ans += to_string(tg);
        else
            ans += ("0"+to_string(tg));

        return ans;
    }
};

class Solution {
    public string timeGap(string st, string et) {
        string ans = "";

        int sh = int.Parse(st.Substring(0, 2));
        int sm = int.Parse(st.Substring(3, 2));
        int ss = int.Parse(st.Substring(6, 2));

        int eh = int.Parse(et.Substring(0, 2));
        int em = int.Parse(et.Substring(3, 2));
        int es = int.Parse(et.Substring(6, 2));

        int startSeconds = sh * 3600 + sm * 60 + ss;
        int endSeconds = eh * 3600 + em * 60 + es;

        int totalSeconds = endSeconds - startSeconds;

        int hours = totalSeconds / 3600;
        totalSeconds %= 3600;
        int minutes = totalSeconds / 60;
        int seconds = totalSeconds % 60;

        ans += (hours > 9 ? $"{hours}:" : $"0{hours}:");
        ans += (minutes > 9 ? $"{minutes}:" : $"0{minutes}:");
        ans += (seconds > 9 ? $"{seconds}" : $"0{seconds}");

        return ans;
    }
}
