// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the number of partitions in the given range
    long long partitions(int l, int r){
        long long c = 0;
        for(int i = 2;i <= sqrt(r);i++){
            for(int j = 2*i;j <= r;j += i){
                if(j >= l){
                    // Incrementing count based on the divisor and quotient
                    if(j/i > sqrt(r))
                        c += i;
                    c += (j/i);
                }
            }
        }

        // Considering the remaining numbers in the range
        for(int i = max(2,l);i <= r;i++)
            c += (i+1);

        // Adding 1 to the count if the lower bound is 1
        if(l == 1) c++;

        // Returning the count of partitions
        return c;
    }
};

class Solution
{
    public long partitions(int l, int r)
    {
        long c = 0;
        for(int i = 2;i <= (int)Math.Sqrt(r);i++){
            for(int j = 2*i;j <= r;j += i){
                if(j >= l){
                    if(j/i > (int)Math.Sqrt(r))
                        c += i;
                    c += (j/i);
                }
            }
        }

        for(int i = Math.Max(2,l);i <= r;i++)
            c += (i+1);

        if(l == 1) c++;

        return c;
    }
}
