class Solution{
    public:
    //Function to count the number of squares that can be formed.
    int countSquare(int B)
    {
	    //subtracting 2 from B and dividing it by 2.
	    B-=2;
	    B/=2;

	    //returning the count of squares using the sum formula.
	    return (B*(B + 1))/2;
    }
};

class Solution{
	// The countSquare method takes an integer B as input and returns the count of squares that can be formed by given number of dots on a 2x2 grid.
	// Subtracting 2 from B to exclude the corner dots to get the number of dots on the edge.
	// Dividing the result by 2 to get the number of rows/columns on the grid.
	// Multiplying the number of rows/columns by the number of rows/columns plus 1 and dividing it by 2 to get the count of squares.
	int countSquare(int B){
		B-=2;
	    B/=2;
	    return (B*(B + 1))/2;
	}
}

