// Function to print a triangle pattern
class Solution {
    public:
    void printTriangle(int n) {
        // iterating over rows
        for (int i = 1; i <= n; i++) {
            // iterating over columns
            for (int j = 1; j <= i; j++) {
                // printing the row number
                cout << i << " ";
            }
            // printing a new line after each row
            cout << endl;
        }
    }
};

java.full_func:
class Solution {

    void printTriangle(int n) {
        for (int i = 1; i <= n; i++) { // loop to print each row of the triangle
            for (int j = 1; j <= i; j++) { // loop to print each element in the row
                System.out.print(i + " "); // print the current row number
            }
            System.out.println(); // move to the next line after printing the row
        }
    }
}

