class Solution {

    void printTriangle(int n) {
        // loop for each row, starting from n and decrementing by 1
        for(int i=n;i>=1;i--){
	      // loop for each number in a row, starting from 1 and incrementing by 1
	      for(int j=1;j<=i;j++){
	          System.out.print(j+" "); // print the number and a space
	      }
	      System.out.println(); // move to the next line
	  }
    }
}

#Back-end complete function Template for Python 3

class Solution:
    def printTriangle(self, N):
        #looping over the range from N to 0 with step size -1
        for i in range(N, 0, -1):
            #looping over the range from 1 to i+1
            for j in range(1, i + 1):
                #printing the value of j with a space after it
                print(j, end = ' ')
            #printing a new line after each row of numbers
            print()

